import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle, faHouse, faCaretLeft, faCaretRight, faGasPump } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import { FamilyCar, FuelType } from '../models';
import { useContext } from 'react';
import { CarsContext } from '../CarsContext';

export const CarEngineForm = () => {
    const { car, setCar } = useContext(CarsContext); // Access the car and setCar properties from the CarsContext

    const isFamilyCar = car instanceof FamilyCar; 

    const handleFamilyCar = () => {
        setCar(new FamilyCar()); // Update the car property in the context with a new instance of FamilyCar
    };

    return (
        <div>
            <div className='engine-type'>
                <div className='header'>
                    <Link to="/"><h3><FontAwesomeIcon icon={faHouse} /></h3></Link>
                    <div className='progress'>
                        <FontAwesomeIcon className="active" icon={faCircle} />
                        <FontAwesomeIcon className="icon" icon={faCircle} />
                        <FontAwesomeIcon className="icon" icon={faCircle} />
                        <FontAwesomeIcon className="icon" icon={faCircle} />
                        <FontAwesomeIcon className="icon" icon={faCircle} />
                        <FontAwesomeIcon className="icon" icon={faCircle} />
                        <FontAwesomeIcon className="icon" icon={faCircle} />
                    </div>
                </div>

                <h1><FontAwesomeIcon icon={faGasPump} /> Engine {isFamilyCar && <p>This car is a FamilyCar.</p>}</h1>
                <div className=''>
                    <div className='input engine-options'>
                        {Object.values(FuelType).map((fuelType, index) => (
                            <div className='input' key={index}>
                                <h3>{fuelType}</h3>
                            </div>
                        ))}
                    </div>
                </div>
                <div>
                    <Link to="/"><button className='navButton'><FontAwesomeIcon icon={faCaretLeft} /></button></Link>
                    <Link to="/select-color"><button className='navButton'><FontAwesomeIcon icon={faCaretRight} /></button></Link>
                </div>
                <button onClick={handleFamilyCar}>Set FamilyCar</button> {/* Button to set the car as a FamilyCar */}
            </div>
        </div>
    );
};

export default CarEngineForm;
